import React, {Component} from 'react';
import SearchBox from './SearchBox'
import CardList from './CardList'
import {robots} from './robots'

class App extends Component {
	constructor(){
		super()
		this.state = {
			robots: robots,
			searchfield: ''
		}
	}

	onSearchChange(event) {
		this.state.setState(this.state.searchfield, event.target.value)
	}

	render() {
		return (
			<div className='tc'>
				<h1>RoboFriends</h1>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robotsInCard={this.state.robots}/>
			</div> 
		);
	}
};



export default App;
