import React, {Component} from 'react';
import { connect } from 'react-redux'
import SearchBox from '../components/SearchBox'
import CardList from '../components/CardList'
import Scroll from '../components/Scroll'
import ErrorBoundry from '../components/ErrorBoundry'
import './App.css'

import  { setSearchField } from '../actions'

const mapStateToProps = (state) => {  // Connecting State from Reducers to Props - Props is searchField
	return {
		searchField: state.searchField // state.searchRobots.state
	}
}

const mapDispatchToProps = (dispatch) => { // Connecting Action From Actions to Props - Props is onSearchChange
	return {
		onSearchChange: (event) => dispatch(setSearchField(event.target.value))
	}
}

class App extends Component {
	constructor(){
		super()
		this.state = {
			robots: []
		}
	}

	componentDidMount() {
		fetch('https://jsonplaceholder.typicode.com/users')
			.then(resp => resp.json())
			.then(users => this.setState({robots: users}))
	}

	render() {
		const { robots } = this.state
		const { searchField, onSearchChange } = this.props // We declared them in mapStateToProps, mapDispatchToProps
		const filteredRobots = robots.filter(robot => {
			return robot.name.toLowerCase().includes(searchField.toLowerCase())
		})
		return !robots.length ? <h1>Loading</h1> :
		(
			<div className='tc'>
				<h1 className='f1'>RoboFriends</h1>
				<SearchBox searchChange={onSearchChange}/>
				<Scroll>
					<ErrorBoundry>
				 		<CardList robotsInCard={filteredRobots}/>
					</ErrorBoundry>
				</Scroll>
			</div> 
		);
	}

};

export default connect(mapStateToProps, mapDispatchToProps)(App);
